var http = require("http");

exports.createServer = function (port) {
	var server = http.createServer(function (req, resp) {
		resp.writeHead(200, { 'Content-Type': 'application/json' });
		resp.end("foo([" + JSON.stringify({
			id: 1,
			lat: 20,
			long: 130,
			timestamp: 0,
			user_id: 1
			
		}) + "]);");
	});

	if (port) {
		server.listen(port);
	}

	return server;
}
